       LYNXBOOL CAdMob::vCreate(const CCreationDesc& desc)            {						                CAdCreationDesc* Desc = (CAdCreationDesc*)(&desc);                m_ADView = [[GADBannerView alloc] initWithFrame:CGRectMake(Desc->m_Position.x,                                                                            Desc->m_Position.y,                                                                           Desc->m_Size.x,                                                                           Desc->m_Size.y)];                m_ADView.rootViewController = ((LynxEngine::GameFramework::iOS::CKernel*)glpKernel)->GetlpViewController();                [m_ADView.rootViewController.view addSubview:m_ADView];                m_ADView.adUnitID = @"ID created when registering my app";                m_ADView.backgroundColor = [UIColor colorWithWhite:1.0f alpha:1.0f];                m_ADView.opaque = true;                                m_Rect.x1 = Desc->m_Position.x;                m_Rect.y1 = Desc->m_Position.y;                m_Rect.x2 = m_Rect.x1 + Desc->m_Size.x;                m_Rect.y2 = m_Rect.y1 + Desc->m_Size.y;                                return LYNX_TRUE;            }            //-------------------------------------------------------------------------------------------------------            //            //  ª°©˙:               //-------------------------------------------------------------------------------------------------------	            LYNXBOOL CAdMob::vInit()            {                GADRequest *request = [GADRequest request];                                request.testDevices = [NSArray arrayWithObjects:                                       GAD_SIMULATOR_ID,                               // Simulator                                       //@"28ab37c3902621dd572509110745071f0101b124",    // Test iPhone 3G 3.0.1                                       @"9b87ca57eeef708cc5d33350f62479dbd2c5bd06",    // Test iPod 4.3.1                                       nil];                if (m_ADView)                    [m_ADView loadRequest:request];                                return LYNX_TRUE;            }            //-------------------------------------------------------------------------------------------------------            //            //  ª°©˙:               //-------------------------------------------------------------------------------------------------------	            void CAdMob::vLoop(float step)            {            }            //-------------------------------------------------------------------------------------------------------            //            //  ª°©˙:               //-------------------------------------------------------------------------------------------------------	            void CAdMob::vRender()            {		            }            //-------------------------------------------------------------------------------------------------------            //            //  ª°©˙:               //-------------------------------------------------------------------------------------------------------	            LYNXBOOL CAdMob::vDestroy()            {		                return LYNX_TRUE;            }		}	}}